glsl3.frag:
precision highp float;
vec4 CCSampleWithAlphaSeparated(sampler2D tex, vec2 uv) {
#if CC_USE_EMBEDDED_ALPHA
  return vec4(texture(tex, uv).rgb, texture(tex, uv + vec2(0.0, 0.5)).r);
#else
  return texture(tex, uv);
#endif
}
#if USE_ALPHA_TEST
  layout(std140) uniform ALPHA_TEST_DATA {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
    if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
    if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 color;
#if USE_TEXTURE
  in vec2 uv0;
  uniform sampler2D cc_spriteTexture;
#endif
layout(std140) uniform Constants {
  float grayEffect;
  float time;
  vec2  resolution;
  float enableFilmEffect;
};
float random(vec2 co) {
  return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453123);
}
vec4 frag () {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    o *= CCSampleWithAlphaSeparated(cc_spriteTexture, uv0);
    if (enableFilmEffect > 0.5) {
      o.r *= 1.15;
      o.g *= 1.05;
      o.b *= 0.85;
      o.rgb = pow(o.rgb, vec3(0.9)) * 1.2;
      vec2 noiseUV = uv0 * resolution * 0.5 + time * 0.5;
      float grainR = random(noiseUV + vec2(0.2, 0.4));
      float grainG = random(noiseUV + vec2(1.0, 2.0));
      float grainB = random(noiseUV + vec2(3.1, 1.7));
      vec3 grain = vec3(grainR, grainG, grainB) * 0.15 - 0.075;
      o.rgb += grain;
      float dist = distance(uv0, vec2(0.5));
      float vignette = smoothstep(0.85, 0.35, dist);
      o.rgb *= vignette;
    }
    float gray = dot(o.rgb, vec3(0.2126, 0.7152, 0.0722));
    o.rgb = mix(o.rgb, vec3(gray), grayEffect);
  #endif
  o *= color;
  ALPHA_TEST(o);
  return o;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

